import React from 'react'
import { Formik, Form, Field } from 'formik';
import * as Yup from 'yup';
import FormikControl from './FormikControl.js'
import TimePicker from 'react-time-picker';

const DishForm = () => {

	const dropdownOptions = [
		{ key:'Choose a dish', value: '' },
		{ key:'Pizza', value: 'pizza' },
		{ key:'Soup', value: 'soup' },
		{ key:'Sandwich', value: 'sandwich' }
]

	const checkboxOptions = [
		{ key:'HTML', value: 'html' },
		{ key:'CSS', value: 'css' },
		{ key:'JavaScript', value: 'javascript' }
]

	const initialValues = {
		dish: '',
		preparationTime: date(),
		typeOfDish: '',
		radioOption: '',
		skills: []
	}

	const validationSchema = Yup.object({
		dish: Yup.string().required('Required'),
		preparationTime: Yup.date().required('Required'),
		typeOfDish: Yup.string().required('Required')
	})

	const onSubmit = values => {
		console.log('Form data', initialValues)
	}

	return (
		<Formik onSubmit={onSubmit} initialValues={initialValues} validationSchema={validationSchema} >
			{
				formik => {
					return <Form className='form-control'>

					<FormikControl
						control='input'
						type='text'
						label='Dish name'
						name='dish'
					/>

					<FormikControl isValid
						control='input'
						type='date'
						component={TimePicker}
						name="preparationTime"
						label="Preparation time"
						ampm={false}
						openTo="hours"
						views={['hours', 'minutes', 'seconds']}
						format="HH:mm:ss"
					/>

					<FormikControl 
						control='select'
						label='Type of dish'
						name='typeOfDish'
						options={dropdownOptions}
					/>

					<FormikControl 
						control='checkbox'
						label='Your skillset'
						name='skills'
						options={checkboxOptions}
					/>

						<button type='submit' disabled={!formik.isValid}>Submit</button>
					</Form>
				}
			}
		</Formik>
	)
}

export default DishForm
